INSERT INTO communication_templates (template_name, template_type, subject, content, variables, case_types, communication_stage, recipient_type) VALUES
('High-Value Case Intake Email', 'email', 'Douglas Hicks Law - Your Case Evaluation', 'Dear {{client_name}}, Thank you for contacting Douglas Hicks Law. Based on our evaluation, you may have a strong case. Our firm has secured a $4.9 billion GM verdict and $8 million police brutality settlements. We are scheduling an urgent consultation.', '{"client_name": "text", "case_type": "text", "attorney_name": "text"}', ARRAY['personal_injury', 'civil_rights', 'medical_malpractice'], 'intake', 'client');