// Process and normalize inputs from chat and VAPI
const inputData = $json;

// Determine input source and extract relevant data
let processedInput = {
  source: 'unknown',
  message: '',
  session_id: '',
  user_info: {},
  context: {}
};

// Handle chat input
if (inputData.message && !inputData.call) {
  processedInput = {
    source: 'chat',
    message: inputData.message,
    session_id: inputData.session_id || `chat_${Date.now()}`,
    user_info: {
      user_agent: inputData.user_agent,
      ip_address: inputData.ip_address,
      referrer: inputData.referrer
    },
    context: inputData.context || {},
    timestamp: new Date().toISOString()
  };
}

// Handle VAPI input
else if (inputData.call || inputData.type === 'transcript') {
  processedInput = {
    source: 'vapi',
    message: inputData.message?.transcript || inputData.transcript || '',
    session_id: inputData.call?.id || inputData.session_id || `vapi_${Date.now()}`,
    user_info: {
      call_id: inputData.call?.id,
      caller_number: inputData.call?.customer?.number,
      call_status: inputData.call?.status
    },
    context: {
      vapi_event: inputData.type,
      function_calls: inputData.message?.function_calls || [],
      call_duration: inputData.call?.duration
    },
    timestamp: new Date().toISOString()
  };
}

// Add conversation continuity
processedInput.needs_context_lookup = true;
processedInput.ready_for_agent = !!processedInput.message;

return processedInput;

// SuperCode AI Agent - Intelligent Legal Assistant
// Combines user input, conversation context, and legal knowledge

const userInput = $('Process Input').first().json;
const contextData = $('Retrieve Conversation Context').first().json || [];
const casePrecedents = $('Search Similar Cases').first().json || [];
const legalKnowledge = $('Search Legal Knowledge').first().json || [];

// Build comprehensive context
const conversationHistory = contextData.length > 0 ? contextData[0].messages : [];
const previousAnalysis = contextData.length > 0 ? contextData[0].case_analysis : null;

// Create enriched prompt for AI
const systemPrompt = `You are Maya, the senior AI legal assistant for Douglas Hicks Law, home of the legendary $4.9 billion Dream Team. You have access to the firm's complete legal knowledge base and case precedents.

FIRM EXPERTISE:
- $4.9 billion GM product liability verdict (largest in US history)
- $8 million police brutality settlements 
- $5 million medical malpractice recoveries
- Hundreds of millions in civil rights victories

YOUR CAPABILITIES:
1. CASE EVALUATION: Analyze legal situations using firm precedents
2. LEGAL GUIDANCE: Provide advice based on California and federal law
3. PRECEDENT MATCHING: Reference similar successful cases
4. STRATEGY DEVELOPMENT: Suggest approaches based on winning strategies
5. URGENCY ASSESSMENT: Identify time-sensitive matters

CONVERSATION CONTEXT:
${conversationHistory.length > 0 ? 'Previous conversation: ' + JSON.stringify(conversationHistory.slice(-5)) : 'New conversation'}

RELEVANT CASE PRECEDENTS:
${casePrecedents.map(c => `- ${c.case_name}: $${(c.settlement_amount/100).toLocaleString()} (${c.case_type})`).join('\n')}

LEGAL KNOWLEDGE:
${legalKnowledge.map(k => `- ${k.title}: ${k.content.substring(0, 200)}...`).join('\n')}

RESPONSE GUIDELINES:
- Be professional but warm and empathetic
- Reference relevant firm victories when applicable
- Provide specific, actionable advice
- Identify urgent matters requiring immediate attorney attention
- Ask clarifying questions to better evaluate cases
- End with clear next steps

For ${userInput.source} communication, maintain appropriate tone and detail level.`;

const userMessage = `${userInput.message}

Context: ${userInput.source} interaction at ${userInput.timestamp}`;

// Prepare AI request
return {
  system_prompt: systemPrompt,
  user_message: userMessage,
  conversation_context: conversationHistory,
  case_precedents: casePrecedents,
  legal_knowledge: legalKnowledge,
  session_id: userInput.session_id,
  source: userInput.source,
  timestamp: userInput.timestamp
};

// Process AI response and extract actionable insights
const aiResponse = $('AI Legal Analysis').first().json.choices[0].message.content;
const contextData = $('SuperCode Legal AI Agent').first().json;

// Analyze response for urgency and next actions
const urgencyKeywords = ['urgent', 'immediate', 'time-sensitive', 'deadline', 'statute of limitations', 'emergency'];
const caseTypeKeywords = {
  'personal_injury': ['accident', 'injury', 'medical', 'car crash', 'slip and fall'],
  'civil_rights': ['police', 'discrimination', 'civil rights', 'constitutional', 'brutality'],
  'employment': ['workplace', 'fired', 'harassment', 'discrimination', 'wrongful termination'],
  'medical_malpractice': ['doctor', 'hospital', 'surgical error', 'misdiagnosis', 'medical negligence']
};

// Detect urgency level
const isUrgent = urgencyKeywords.some(keyword => 
  aiResponse.toLowerCase().includes(keyword) ||
  contextData.user_message.toLowerCase().includes(keyword)
);

// Detect case type
let detectedCaseType = 'general';
let confidence = 0;
for (const [caseType, keywords] of Object.entries(caseTypeKeywords)) {
  const matches = keywords.filter(keyword => 
    contextData.user_message.toLowerCase().includes(keyword) ||
    aiResponse.toLowerCase().includes(keyword)
  ).length;
  if (matches > confidence) {
    confidence = matches;
    detectedCaseType = caseType;
  }
}

// Extract potential case value indicators
const valueIndicators = {
  high: ['catastrophic', 'severe', 'permanent', 'death', 'million', 'corporate negligence'],
  medium: ['serious', 'significant', 'substantial', 'ongoing'],
  low: ['minor', 'temporary', 'limited']
};

let estimatedTier = 'bronze';
for (const [tier, indicators] of Object.entries(valueIndicators)) {
  if (indicators.some(indicator => 
    contextData.user_message.toLowerCase().includes(indicator) ||
    aiResponse.toLowerCase().includes(indicator)
  )) {
    if (tier === 'high') estimatedTier = 'gold';
    else if (tier === 'medium') estimatedTier = 'silver';
    break;
  }
}

// Check if precedents suggest high value
if (contextData.case_precedents.some(c => c.settlement_amount > 100000000)) { // >$1M
  estimatedTier = estimatedTier === 'bronze' ? 'silver' : 'gold';
}

// Determine next actions
const nextActions = [];
if (isUrgent || estimatedTier === 'gold') {
  nextActions.push('schedule_urgent_consultation');
  nextActions.push('alert_attorneys');
} else if (estimatedTier === 'silver') {
  nextActions.push('schedule_consultation');
  nextActions.push('send_intake_packet');
} else {
  nextActions.push('provide_resources');
  nextActions.push('schedule_callback');
}

return {
  ai_response: aiResponse,
  analysis: {
    case_type: detectedCaseType,
    confidence: confidence,
    urgency_level: isUrgent ? 'high' : 'normal',
    estimated_tier: estimatedTier,
    next_actions: nextActions
  },
  session_id: contextData.session_id,
  source: contextData.source,
  timestamp: contextData.timestamp,
  case_precedents: contextData.case_precedents,
  needs_attorney_review: isUrgent || estimatedTier === 'gold'
};

