// Format final response based on source (chat vs VAPI)
const responseData = $json;

if (responseData.source === 'chat') {
  // Chat response format
  return {
    message: responseData.ai_response,
    analysis: responseData.analysis,
    session_id: responseData.session_id,
    timestamp: responseData.timestamp,
    next_actions: responseData.analysis.next_actions,
    precedents_found: responseData.case_precedents.length,
    status: 'success'
  };
} else if (responseData.source === 'vapi') {
  // VAPI response format with function calls if needed
  const functionCalls = [];

  if (responseData.analysis.next_actions.includes('schedule_urgent_consultation')) {
    functionCalls.push({
      name: 'scheduleConsultation',
      parameters: {
        urgencyLevel: 'urgent',
        caseValue: responseData.analysis.estimated_tier,
        consultationType: 'immediate'
      }
    });
  }

  if (responseData.analysis.next_actions.includes('alert_attorneys')) {
    functionCalls.push({
      name: 'transferToAttorney', 
      parameters: {
        transferReason: 'high_value_case',
        specialtyNeeded: responseData.analysis.case_type
      }
    });
  }

  return {
    message: responseData.ai_response,
    function_calls: functionCalls,
    session_context: {
      case_type: responseData.analysis.case_type,
      urgency: responseData.analysis.urgency_level,
      tier: responseData.analysis.estimated_tier
    },
    timestamp: responseData.timestamp
  };
}

// Default response
return {
  message: responseData.ai_response,
  session_id: responseData.session_id,
  timestamp: responseData.timestamp
};
